mapper.select:
SELECT *
FROM waltz.TB_MENU_INFO
WHERE MENU_TP = "M"
AND MENU_CD IN (
	SELECT MENU_CD
	FROM waltz.TB_AUTH_MAPP_INFO
	WHERE USER_GRP_CD = #{userGrpCd, jdbcType=VARCHAR}
	AND SEL_YN = "Y"
) ORDER BY MENU_SORT_NO;

SELECT *
FROM waltz.TB_MENU_INFO
WHERE MENU_TP = "S"
AND MENU_CD IN (
	SELECT MENU_CD
	FROM waltz.TB_AUTH_MAPP_INFO
	WHERE USER_GRP_CD = #{userGrpCd, jdbcType=VARCHAR}
	AND SEL_YN = "Y"
) ORDER BY MENU_SORT_NO;
